import React from "react";
import { Stage, Layer, Rect, Text } from "react-konva";
import Konva from "konva";
import ColoredRect from "./ColoredRect";

class Barchart extends React.Component {
 render() {
  const data = {
   bar01: { value: 50, label: "AAA" },
   bar02: { value: 10, label: "BBB" },
   bar03: { value: 90, label: "CCC" },
   bar04: { value: 120, label: "CCC" }
  };

  // function maxHeightCalc () 

  const maxH = () => {
    let h = 0;
    Object.keys(data).map( key => {
      console.log(data[key].value);
      if (data[key].value > h) {
        h = data[key].value ;
      }
    })
    return h;
  }


  const gutter = 4;
  const wval = 500 / Object.keys(data).length;

  return (
   <Stage width={500} height={500}>
    <Layer>
     {Object.keys(data).map((key, index) => (
      <ColoredRect key={key} maxH={maxH} xpos={wval * index} hval={data[key].value} />
     ))}
    </Layer>
   </Stage>
  );
 }
}

export default Barchart;
