import React from "react";
import { Stage, Layer, Rect, Line } from "react-konva";
import Konva from "konva";
import ColoredRect from "./ColoredRect";

class Barchart extends React.Component {
  renderBackground = (theme, height, width, gutter) => {
    if (theme === "light") return null;
    else {
      return <Rect x={gutter} y={gutter} width={width} height={height} fill={black} />;
    }
  };

  renderYAxisTickMarks = (gutter, height) => {
    const numYMarks = 10;
    const yAxisTickDistance = height / numYMarks + 1; // so we dont start at the x axis.
    var arr = [];
    for (var i = 0; i < numYMarks; i++) {
      arr.push(
        <Line
          key={"mark" + i}
          points={[
            gutter - 3,
            yAxisTickDistance * i,
            this.props.width + 3,
            yAxisTickDistance * i
          ]}
          stroke={"black"}
          strokeWidth={1}
          tension={1}
          dash={[4]}
        />
      );
    }
    return arr;
  };

  render() {
    const data = this.props.data;

    const theme = this.props.theme != null ? this.props.theme : "light";
    const showMarks = !this.props.showMarks ? false : true;

    const gutter = 10;
    const width = this.props.width - gutter;
    const height = this.props.height - gutter;

    function maxHeightCalc() {
      let h = 0;
      Object.keys(data).map(key => {
        if (data[key].value > h) {
          h = data[key].value;
        }
      });
      return h;
    }

    // highest bar should fill up the barchart height. All bars should be scaled
    const scale = height / maxHeightCalc();
    // x posiiton of the bar
    const xPos = width / Object.keys(data).length;
    // space between bars should be 1/2 the width of bars.
    // 4 bars = 3 spaces. -> 4 + 4 + 3 = 11. Width /11 = each space. And a bar is twice that.
    const barWidth =
      2 *
      (width / (Object.keys(data).length * 2 + (Object.keys(data).length - 1)));

    return (
      <Stage width={this.props.width} height={this.props.height}>
        <Layer>
          {this.renderBackground(theme, height, width, gutter)}

          {this.renderYAxisTickMarks(gutter, height)}

          {/* Bars */}
          {Object.keys(data).map((key, index) => (
            <ColoredRect
              key={key}
              xPos={xPos * index + gutter}
              yPos={height - scale * data[key].value}
              height={scale * data[key].value}
              width={barWidth}
              value={data[key].value}
              chartHeight={height} // used for labels
            />
          ))}

          <Line
            // horisontal x axis
            points={[gutter, height, this.props.width, height]}
            stroke={"black"}
            strokeWidth={1}
            tension={1}
          />

          <Line
            // vertical Y axis
            // [x1, y1, x2, y2, x3, y3]
            points={[gutter, 0, gutter, height]}
            stroke={"black"}
            strokeWidth={1}
            tension={1}
          />
        </Layer>
      </Stage>
    );
  }
}

export default Barchart;
