import React from "react";
import { Stage, Layer, Rect, Line, Text } from "react-konva";
import Konva from "konva";
import Plotmark from "./Plotmark";

class Scatterplot extends React.Component {
  renderBackground = (height, width, gutter) => {
    return (
      <Rect
        x={0}
        y={0}
        width={width + gutter}
        height={height}
        fill={this.props.theme.bgcol}
      />
    );
  };

  renderYAxisTickMarks = (gutter, height) => {
    if (this.props.theme.showYTicks) {
      const numYMarks = 11;
      const yAxisTickDistance = height / numYMarks + 1; // so we dont start at the x axis.
      var arr = [];
      for (var i = 0; i < numYMarks; i++) {
        arr.push(

            <Line
              key={"mark" + i}
              points={[
                gutter,
                yAxisTickDistance * i,
                this.props.width + 3,
                yAxisTickDistance * i
              ]}
              stroke={this.props.theme.col}
              strokeWidth={1}
              tension={1}
              dash={[4]}
              onClick={this.handleLineClick} // NOT WORKING
            />
        );
      }
      return arr;
    } else return null;
  };

  // yDomainMax = highest value. 
  renderYAxis = (gutter, height, yDomainMax) => {
    if (this.props.theme.showYAxis) {
      const numYMarks = 11;
      const domainTicks = parseInt(yDomainMax / (numYMarks-1));
      const yAxisTickDistance = height / numYMarks + 1; // so we dont start at the x axis.
      var arr = [];
      for (var i = 0; i < numYMarks; i++) {
        arr.push(

            <Text
              key={"text" + i}
              text={domainTicks * (numYMarks - i)}
              x={gutter - 10}
              y={yAxisTickDistance * i - 5}
              fontSize={10}
            />

        );
      }
      return arr;
    } else return null;
  };

  render() {
    const data = this.props.data;
    let gutter = 0;
    if (this.props.theme.showYTicks) {
      gutter = 30;
    }

    const width = this.props.width - gutter;
    const height = this.props.height - gutter;

    function maxHeightCalc() {
      let h = 0;
      Object.keys(data).map(key => {
        if (data[key].rating > h) {
          h = data[key].rating;
        }
      });
      return h + 10;
    }

    function maxWidthCalc() {
      let w = 0;
      Object.keys(data).map(key => {
        if (data[key].years > w) {
          w = data[key].years;
        }
      });
      return w;
    }

    // highest bar should fill up the barchart height. All bars should be scaled
    const hScale = height / maxHeightCalc();
    const wScale = width / maxWidthCalc();

    return (
      <Stage width={this.props.width} height={this.props.height}>
        <Layer>
          {this.renderBackground(height, width, gutter)}
          {this.renderYAxisTickMarks(gutter, height)}
          {this.renderYAxis(gutter, height, maxHeightCalc())}


          {/* plot marks */}
          {Object.keys(data).map((key, index) => (
            <Plotmark
              key={key}
              xPos={data[key].years * wScale}
              yPos={height - data[key].rating * hScale}
              radius={5}
              color={this.props.theme.barcols[0]}
              value={
                data[key].name +
                "--- Rating: " +
                data[key].rating +
                " and " +
                "Years: " +
                data[key].years
              }
            />
          ))}

          <Text
            text={"Rating"}
            x={17}
            y={height / 2 - 20}
            fontSize={15}
            rotation={90}
          />

          <Text
            text={"Years"}
            x={width / 2 - 20}
            y={height - 17}
            fontSize={15}
          />
        </Layer>
      </Stage>
    );
  }
}

export default Scatterplot;
