import React from "react";
import { Rect, Text } from "react-konva";
import Konva from "konva";

class ColoredRect extends React.Component {
  state = {
    color: "lightblue"
  };
  handleClick = () => {
    this.setState({
      color: Konva.Util.getRandomColor()
    });
    // alert(this.props.value);
  };

  renderText = () => {
    // Text reference: https://konvajs.org/docs/shapes/Text.html
    const textXPosition = this.props.xPos + this.props.width / 2 - 10;
    const textYPosition = this.props.chartHeight - 20;

    // only show text if there is room to show it. 
    if (this.props.width > 50) {
      return (
        <Text text={this.props.value} x={textXPosition} y={textYPosition} fontSize={20}/>
      );
    }
  };

  render() {
    return (
      <>
        <Rect
          x={this.props.xPos}
          y={this.props.yPos}
          width={this.props.width}
          height={this.props.height}
          fill={this.state.color}
          shadowBlur={0}
          onClick={this.handleClick}
        />
        {this.renderText()}
      </>
    );
  }
}

export default ColoredRect;
