import React from "react";
import { Stage, Layer, Line } from "react-konva";
import Konva from "konva";
import ColoredRect from "./ColoredRect";

class Barchart extends React.Component {
  render() {
    const data = this.props.data;

    function maxHeightCalc() {
      let h = 0;
      Object.keys(data).map(key => {
        if (data[key].value > h) {
          h = data[key].value;
        }
      });
      return h;
    }
    // highest bar should fill up the barchart height. All bars should be scaled
    const scale = this.props.height / maxHeightCalc();
    // x posiiton of the bar
    const xPos = this.props.width / Object.keys(data).length;
    // space between bars should be 1/2 the width of bars.
    // 4 bars = 3 spaces. -> 4 + 4 + 3 = 11. Width /11 = each space. And a bar is twice that.
    const barWidth =
      2 *
      (this.props.width /
        (Object.keys(data).length * 2 + (Object.keys(data).length - 1)));

    return (
      <Stage width={this.props.width} height={this.props.height}>
        <Layer>
        <Line
            x={50}
            y={50}
            points={[73, 80, 40, 200]}
            stroke={"red"}
            strokeWidth={5}
            tension={1}
          />

          {Object.keys(data).map((key, index) => (
            <ColoredRect
              key={key}
              xPos={xPos * index}
              yPos={this.props.height - scale * data[key].value}
              height={scale * data[key].value}
              width={barWidth}
              value={data[key].value}
              chartHeight={this.props.height}
            />
          ))}

        </Layer>
      </Stage>
    );
  }
}

export default Barchart;
